import { Card, Typography, Table, Image, Space, Button, Tooltip } from "antd";
import type { ColumnsType } from "antd/es/table";
import { EditOutlined, DeleteOutlined } from "@ant-design/icons";
import { useCategories } from "./hooks/useCategories";
import type { ICategory } from "./types";
import { formatDate } from "@/lib/utils";
import "./category.scss";

const { Title, Text } = Typography;

const FALLBACK_IMAGE =
  "data:image/png;base64,";

export default function CategoriesPage() {
  const { categories, loading } = useCategories();

  const columns: ColumnsType<ICategory> = [
    {
      title: "#",
      width: 70,
      render: (_: unknown, __: ICategory, index: number) => index + 1,
    },
    {
      title: "Ảnh đại diện",
      dataIndex: "thumbnail",
      width: 100,
      render: (thumbnail: string | null | undefined) => (
        <Image
          src={thumbnail || undefined}
          alt="Category thumbnail"
          width={60}
          height={60}
          style={{ objectFit: "cover", borderRadius: 4 }}
          fallback={FALLBACK_IMAGE}
        />
      ),
    },
    {
      title: "Tên",
      dataIndex: "title",
      key: "title",
      render: (title: string, record: ICategory) => (
        <Space direction="vertical" size={4}>
          <Text strong>{title}</Text>
          <Text type="secondary" style={{ fontSize: "12px" }}>
            ID: {record.id}
          </Text>
        </Space>
      ),
    },
    {
      title: "Slug",
      dataIndex: "slug",
      key: "slug",
      render: (slug: string) => <Text code>{slug}</Text>,
    },
    {
      title: "Mô tả",
      dataIndex: "description",
      key: "description",
      render: (description: string | null | undefined) => (
        <Text type="secondary" ellipsis style={{ maxWidth: 300 }}>
          {description || "-"}
        </Text>
      ),
    },
    {
      title: "Ngày tạo",
      dataIndex: "createdAt",
      key: "createdAt",
      render: (date: string | undefined) => (date ? formatDate(date) : "-"),
    },
    {
      title: "Hành động",
      key: "actions",
      width: 120,
      render: (_: unknown, record: ICategory) => (
        <Space>
          <Tooltip title="Chỉnh sửa">
            <Button
              type="text"
              icon={<EditOutlined />}
              onClick={() => {
                // TODO: Implement edit handler
                console.log("Edit category:", record);
              }}
            />
          </Tooltip>
          <Tooltip title="Xóa">
            <Button
              type="text"
              danger
              icon={<DeleteOutlined />}
              onClick={() => {
                // TODO: Implement delete handler
                console.log("Delete category:", record);
              }}
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  return (
    <div className="categories-container">
      <div className="categories-header">
        <Title level={2} style={{ margin: 0 }}>
          Danh mục sách
        </Title>
        <Text type="secondary">
          Quản lý phân loại, thể loại và tag của sách.
        </Text>
      </div>
      <Card>
        <Table
          columns={columns}
          dataSource={categories}
          rowKey="id"
          loading={loading}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Tổng ${total} danh mục`,
          }}
        />
      </Card>
    </div>
  );
}
